import React, {Component} from 'react';
import logo from './logo.svg';
import './Mobile.css';
import './Tablet.css';
import './Desktop.css';
import './Luxury.css';
import './App.css';


const Stops = ( state ) => {
    if (state.stopLoaded) {
        return state.stopData.map(stop => {
            return (
                <li key={stop.Value}>{stop.Text}</li>
            )
        })
    } else {
        return (<div> Awaiting the stops to come! </div>)
    }
}

const Directions = ( state ) => {
    if (!state.directionLoaded) {
        return (null)
    } else {
        return (
            <select id="Directions" name="Direction" onChange={this.changeDirection}>
                <option value={state.directionData[0].Value}>{state.directionData[0].Text}</option>
                <option value={state.directionData[1].Value}>{state.directionData[1].Text}</option>
            </select>
        )
    }
}

const Routes = ( state ) => {
    if (!state.routeLoaded) {
        return (<h1>Route is loading...</h1>)
    } else {
        return (
            <select id="Route" name="Route" onChange={this.handleDirection}>
                        {state.routeData.map(data => (
                            <option key={data.Route} value={data.Route}>{data.Description}</option>
                        ))}
                        <option>{state.routeData[0].description}</option>
                    </select>
        )
    }
}

