class Master extends Component {
    constructor(props) {
        super(props);
        this.state = {
            routeData: [],
            route: '',
            routeLoaded: false,
            routeLoading: false,
            directionData: [],
            direction: '',
            directionLoaded: false,
            directionLoading: false,
            stopData: [],
            stopLoaded: false,
            stopLoading: false,
            error: null
        }
        this.getStops = this.getStops.bind(this);
        this.changeDirection = this.changeDirection.bind(this);
        this.handleDirection = this.handleDirection.bind(this);
    }

    componentDidMount = () => {
        this.setState({ routeLoading: true })

        fetch('https://svc.metrotransit.org/NexTrip/Routes?format=json')
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    routeLoading: false,
                    routeLoaded: true,
                    routeData: result
                })
                console.log("Route is loaded, here is data: ")
                console.log(this.state.routeData)
            }, (error) => {
                this.setState({
                    routeLoaded: false, 
                    routeLoading: false, 
                    error
                });
            })
    }

    getStops = () => {
        console.log("In getStops");
        this.setState({ stopLoading: true })

        console.log("Fetching this route: ")
        console.log('https://svc.metrotransit.org/NexTrip/Stops/' + this.state.route + '/' + this.state.direction + '?format=json')

        fetch('https://svc.metrotransit.org/NexTrip/Stops/' + this.state.route + '/' + this.state.direction + '?format=json')
          .then(res => res.json())
          .then((result) => {
              this.setState({
                  stopData: result,
                  stopLoaded: true
              })
          }, (error) => {
            this.setState({
                stopLoaded: false, 
                stopLoading: false, 
                error
            });
        })
    }

    changeDirection = ( event ) => {
        this.setState({
            direction: event.target.value
        }, this.getStops);
    }

    handleDirection = (event) => {
        console.log("In handleDirection")
        // Update state values
        this.setState({
            route: event.target.value,
            directionLoading: true,
            directionLoaded: false
        })

        console.log("Updated state.route to")
        console.log(event.target.value)
        console.log('In handle direction');

        fetch('https://svc.metrotransit.org/NexTrip/Directions/' + event.target.value + '?format=json')
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    directionLoaded: true,
                    directionLoading: false,
                    directionData: result,
                    direction: result[0].Value
                })
                this.getStops();
            }, (error) => {
                this.setState({
                    directionLoaded: false, 
                    directionLoading: false, 
                    error
                });
            })
    }

    render () {
        return (
            <React.Fragment>
            <div className='route-container'>
                {this.state.routeLoaded
                    ? <React.Fragment>
                            <p>Please select a route from the options below to see the directions and stops available for you!</p>
                            <select id="Select-Route" name="Route" onChange={this.handleDirection}>
                                {this.state.routeData.map(data => (
                                    <option key={data.Route} value={data.Route}>{data.Description}</option>
                                ))}
                            </select>
                            <i className="fas fa-arrow-circle-down"></i>
                        </React.Fragment>
                    : <div>Loading</div>
                }

                <div className="directions-container" data-testid="Direction-Container">
                    {this.state.directionLoaded
                        ? <React.Fragment>
                                    <div className="description">Choose a direction: </div>
                                    <input type='radio' name='Direction' id={this.state.directionData[0].Value} value={this.state.directionData[0].Value} onClick={this.changeDirection} defaultChecked ></input><label htmlFor={this.state.directionData[0].Value}>{this.state.directionData[0].Text.toLowerCase()}</label>
                                    <input type='radio' name='Direction' id={this.state.directionData[1].Value} value={this.state.directionData[1].Value} onClick={this.changeDirection}></input><label htmlFor={this.state.directionData[1].Value}>{this.state.directionData[1].Text.toLowerCase()}</label>
                                </React.Fragment>
                        : null
                    }
                </div>
                </div>
                <div className="stops-container" data-testid="Stops-Container">
                    <h2>Stops</h2>
                    {this.state.stopLoaded
                        ? <ul>
                            {this.state.stopData.map(stop => (
                                <li key={stop.Value}>{stop.Text}</li>
                            ))}
                            </ul>
                        : <div>Please choose a route above to see related stops.</div>
                    }

                </div>
            </React.Fragment>
        )
    }
}

