function App() {
    return (
        <div className='content'>
            <div className='note'><h4 className='color-red'>This application is sourced by real-time data from </h4> <img src='https://www.metrotransit.org/img/MetroTransitLogo.svg'></img></div>
            <h1>NexTrip</h1>
            <div className='application'>
                <Master />
            </div>
        </div>
    );
}

export default App;
