const Directions = ({
    direction1Text = "",
    direction1Value = "",
    direction2Text = "",
    direction2Value = ""
}) => {
    if (direction1Text === "" && direction1Value === "" && direction2Text === "" && direction2Value === "") {
        return (null)
    } else {
        return (
            <select id="Directions" name="Direction" onChange="handleDirectionValue">
                <option value={direction1Value}>{direction1Text}</option>
                <option value={direction2Value}>{direction2Text}</option>
            </select>
        )
    }
}


class Routes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            loading: false,
            data: [],
            directionData: [],
            value: '',
            route: '',
            direction: '',
            direction1Text: '',
            direction1Value: '',
            direction2Text: '',
            direction2Value: '',
            stopData: []
        }

        this.handleDirection = this
            .handleDirection
            .bind(this);
    }

    componentDidMount = () => {
        this.setState({loading: true})
        fetch('https://svc.metrotransit.org/NexTrip/Routes?format=json')
            .then(res => res.json())
            .then((result) => {
                this.setState({loading: false, data: result});
            }, (error) => {
                this.setState({loading: false, error});
            })
    }

    handleDirectionValue(event) {
      console.log("Updating Direction value to: " + event.target.value);
      this.setState({directionData: event.target.value});
    }

    handleDirection(event) {
        console.log('Received a change')
        this.setState({route: event.target.value})
        console.log(event.target.value)
        // console.log('Get data for: https://svc.metrotransit.org/NexTrip/Directions/'
        // + )
        fetch('https://svc.metrotransit.org/NexTrip/Directions/' + event.target.value + '?format=json')
            .then(res => res.json())
            .then((result) => {
                console.log("Changing states with new results")
                console.log(result)
                this.setState({
                    loading: false,
                    directionData: result.data,
                    direction: result[0].Value,
                    direction1Text: result[0].Text,
                    direction1Value: result[0].Value,
                    direction2Text: result[1].Text,
                    direction2Value: result[1].Value
                });
            }, (error) => {
                this.setState({loading: false, error});
            })
        console.log(this.state)
        console.log(this.state.directionData)
        fetch('http://svc.metrotransit.org/NexTrip/Stops/' + this.stateroute + '/' + this.state.direction + '?format=json')
          .then(res => res.json())
          .then((result) => {
              console.log("In Stops")
              console.log(result)
              this.setState({
                  stopData: result
              })
          }, (error) => {
              error = error;
          })
        console.log("Stop data: " + this.state.stopData)
    }

    render() {
        const {error, loading, data} = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else {
            return (
                <div>
                    <select id="Route" name="Route" onChange={this.handleDirection}>
                        {data.map(data => (
                            <option key={data.Route} value={data.Route}>{data.Description}</option>
                        ))}
                    </select>
                    <Directions
                        direction1Text={this.state.direction1Text}
                        direction1Value={this.state.direction1Value}
                        direction2Text={this.state.direction2Text}
                        direction2Value={this.state.direction1Value}/>
                </div>
            )
        }
    }
}
//<Stops route={this.state.route} direction={this.state.direction} />
function App() {
    return (
        <div>
            <h1>Next Trip Application</h1>

            <h2>Developed by Amy VandenBrook</h2>
            <Routes/>
        </div>
    );
}

export default App;
