import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

const state = {
    routeData: [],
    route: '',
    routeLoaded: false,
    routeLoading: false,
    directionData: [],
    direction: '',
    directionLoaded: false,
    directionLoading: false,
    stopData: [],
    stopLoaded: false,
    stopLoading: false,
    error: null
}

const Stops = () => {
    return state.stopData.map(stop => {
        return (
            <li key={stop.Value}>{stop.Text}</li>
        )
    })
}

const getStops = () => {
        fetch('https://svc.metrotransit.org/NexTrip/Stops/' + state.route + '/' + state.direction + '?format=json')
          .then(res => res.json())
          .then((result) => {
              state.stopData = result;
              state.stopLoaded = true;
          }, (error) => {
              state.error = error;
          })
    }

const changeDirection = ( event ) => {
    state.direction = event.target.value;
    getStops();
}
// const Directions = ({data=[{Value: 1, Text: 'Northbound'},{Value: 2, Text: 'Southbound'}]}) => 
//     return (
//         <form>
//             <input type="radio" name="direction" key={1} value={data[0].Value}>{data[0].Text}</input>
//             <input type="radio" name="direction" key={2} value={data[1].Value}>{data[1].Text}</input>
//         </form>
//     )
//     // return data.map(direction => {
//     //     return (
//     //         <input id={direction.Value} name="direction" key={direction.Value} value={direction.Value}>{direction.Text}</input>

//     //         )

//     // })
// }

//            <input type="radio" id={direction.Value} name="direction" key={direction.Value}>{direction.Text}</input>



const Directions = () => {
    if (!state.directionLoaded) {
        return (null)
    } else {
        return (
            <select id="Directions" name="Direction" onChange={changeDirection}>
                <option value={state.directionData[0].Value}>{state.directionData[0].Text}</option>
                <option value={state.directionData[1].Value}>{state.directionData[1].Text}</option>
            </select>
        )
    }
}

class Routes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            loading: false,
            data: [],
            directionData: [],
            value: '',
            route: '',
            direction: '',
            direction1Text: '',
            direction1Value: '',
            direction2Text: '',
            direction2Value: '',
            stopData: []
        }

        this.handleDirection = this
            .handleDirection
            .bind(this);
    }

    componentDidMount = () => {
        state.routeLoading = true
        fetch('https://svc.metrotransit.org/NexTrip/Routes?format=json')
            .then(res => res.json())
            .then((result) => {
                state.routeLoading = false;
                state.routeLoaded = true;
                state.routeData = result;
                console.log("Received data")
                console.log(state.routeData)
            }, (error) => {
                state.error = error;
            })
    }

    handleDirection(event) {
        // Update state values
        state.route = event.target.value;
        state.directionLoading = true;
        state.directionLoaded = false;

        console.log("Updated state.route to")
        console.log(state.route)

        fetch('https://svc.metrotransit.org/NexTrip/Directions/' + state.route + '?format=json')
            .then(res => res.json())
            .then((result) => {
                state.directionLoaded = true;
                state.directionLoading = false;
                state.directionData = result;
                state.direction = result[0].Text;
                getStops();
            }, (error) => {
                state.error = error
            })
    }

    render() {
        if (state.error) {
            return <div>Error: {state.error.message}</div>;
        } else {
            return (
                <div>
                    <div>{state.routeDataLoaded}</div>
                    <select id="Route" name="Route" onChange={this.handleDirection}>
                        {state.routeData.map(data => (
                            <option key={data.Route} value={data.Route}>{data.Description}</option>
                        ))}
                    </select>
                    <Directions />
                    <div className="stops-container">
                        <ul>
                            <Stops />
                        </ul>
                    </div>

                </div>
            )
        }
    }
}
//<Directions
// direction1Text={this.state.direction1Text}
// direction1Value={this.state.direction1Value}
// direction2Text={this.state.direction2Text}
// direction2Value={this.state.direction1Value}/>
//<Stops route={this.state.route} direction={this.state.direction} />
function App() {
    return (
        <div>
            <h1>Next Trip Application</h1>

            <h2>Developed by Amy VandenBrook</h2>
            <Routes/>
        </div>
    );
}

export default App;
