class Master extends Component {
    constructor(props) {
        super(props);
        this.state = {
            routeData: [],
            route: '',
            routeLoaded: false,
            routeLoading: false,
            directionData: [],
            direction: '',
            directionLoaded: false,
            directionLoading: false,
            stopData: [],
            stopLoaded: false,
            stopLoading: false,
            error: null
        }
        this.getStops = this.getStops.bind(this);
        this.changeDirection = this.changeDirection.bind(this);
        this.handleDirection = this.handleDirection.bind(this);
    }

    componentDidMount = () => {
        this.setState({ routeLoading: true })

        fetch('https://svc.metrotransit.org/NexTrip/Routes?format=json')
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    routeLoading: false,
                    routeLoaded: true,
                    routeData: result
                })
                console.log("Route is loaded, here is data: ")
                console.log(this.state.routeData)
            }, (error) => {
                this.setState({
                    routeLoaded: false, 
                    routeLoading: false, 
                    error
                });
            })
    }

    getStops = () => {
        console.log("In getStops");
        this.setState({ stopLoading: true })

        fetch('https://svc.metrotransit.org/NexTrip/Stops/' + this.state.route + '/' + this.state.direction + '?format=json')
          .then(res => res.json())
          .then((result) => {
              this.setState({
                  stopData: result,
                  stopLoaded: true
              })
          }, (error) => {
            this.setState({
                stopLoaded: false, 
                stopLoading: false, 
                error
            });
        })
    }

    changeDirection = ( event ) => {
        this.setState({
            direction: event.target.value
        })
        this.getStops();
    }

    handleDirection = (event) => {
        console.log("In handleDirection")
        // Update state values
        this.setState({
            route: event.target.value,
            directionLoading: true,
            directionLoaded: false
        })

        console.log("Updated state.route to")
        console.log(event.target.value)
        console.log('In handle direction');

        fetch('https://svc.metrotransit.org/NexTrip/Directions/' + this.state.route + '?format=json')
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    directionLoaded: true,
                    directionLoading: false,
                    directionData: result,
                    direction: result[0].Text
                })
                this.getStops();
            }, (error) => {
                this.setState({
                    directionLoaded: false, 
                    directionLoading: false, 
                    error
                });
            })
    }

    render () {
        return (
            <React.Fragment>
                {this.state.routeLoaded
                    ? <select id="Route" name="Route" onChange={this.handleDirection}>
                            {this.state.routeData.map(data => (
                                <option key={data.Route} value={data.Route}>{data.Description}</option>
                            ))}
                        </select>
                    : <div>Loading</div>
                }

                <Directions state={this} />
                <div className="stops-container">
                    <ul>
                        <Stops state={this} />
                    </ul>
                </div>
            </React.Fragment>
        )
    }
}

function App() {
    return (
        <div>
            <h1>Next Trip Application</h1>

            <h2>Developed by Amy VandenBrook</h2>
            <Master />
        </div>
    );
}

export default App;
