import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import { Header } from './Header'

class Routes extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      loading: false,
      data: []
    }
  }

  componentDidMount = () => {
    this.setState({loading: true})
    fetch('https://svc.metrotransit.org/NexTrip/Routes?format=json')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            loading: false,
            data: result
          });
        },

        (error) => {
          this.setState({
            loading: false,
            error
          });
        }
      )
  }

  render() {
    const { error, loading, data } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else{
      return (
        <ul>
          {data.map(data => (
            <li key={data.Route}>
              {data.Description}
            </li>
          ))}
        </ul>
      )
    }
  }
}

function App() {
  return (
    <div>
      <h1>Next Trip Application</h1>

      <h2>Developed by Amy VandenBrook</h2>
      <Routes />
    </div>
  );
}

export default App;
