import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

const Directions = ({direction1Text="", direction1Value="", direction2Text="", direction2Value=""}) => {
    return (
        <select id="Directions" name="Direction">
          <option key={direction1Value} value={direction1Value}>{direction1Text}</option>
          <option key={direction2Value} value={direction2Value}>{direction2Text}</option>
        </select>
    )
}

class Routes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            loading: false,
            data: [],
            directionData: [],
            value: '',
            route: '',
            direction: ''
        }

        this.handleDirection = this
            .handleDirection
            .bind(this);
    }

    componentDidMount = () => {
        this.setState({loading: true})
        fetch('https://svc.metrotransit.org/NexTrip/Routes?format=json')
            .then(res => res.json())
            .then((result) => {
                this.setState({loading: false, data: result});
            }, (error) => {
                this.setState({loading: false, error});
            })
    }

    handleDirection(event) {
        console.log('Received a change')
        this.setState({route: event.target.value})
        console.log(event.target.value)
        // console.log('Get data for: https://svc.metrotransit.org/NexTrip/Directions/' +
        // )
        fetch('https://svc.metrotransit.org/NexTrip/Directions/' + event.target.value + '?format=json')
            .then(res => res.json())
            .then((result) => {
                console.log(result)
                this.setState({loading: false, directionData: result.data});
            }, (error) => {
                this.setState({loading: false, error});
            })
        console.log(this.state)
        console.log(this.state.directionData)
    }

    render() {
        const {error, loading, data} = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else {
            return (
                <div>
                    <select id="Route" name="Route" onChange={this.handleDirection}>
                        {data.map(data => (
                            <option key={data.Route} value={data.Route}>{data.Description}</option>
                        ))}
                    </select>
                    <Directions directions={this.state.directionData} />
                </div>
            )
        }
    }
}

function App() {
    return (
        <div>
            <h1>Next Trip Application</h1>

            <h2>Developed by Amy VandenBrook</h2>
            <Routes/>
        </div>
    );
}

export default App;
